function NavToggle() {
    $(".navbar-minimalize").trigger("click");
}

function SmoothlyMenu() {
    $("body").hasClass("mini-navbar") ? $("body").hasClass("fixed-sidebar") ? ($("#side-menu").hide(), setTimeout(function() {
        $("#side-menu").fadeIn(500)
    },
    300)) : $("#side-menu").removeAttr("style") : ($("#side-menu").hide(), setTimeout(function() {
        $("#side-menu").fadeIn(500)
    },
    100))
}

function localStorageSupport(){
    return (('localStorage' in window) && window['localStorage'] !== null);
}

$(document).ready(function(){
    function a() {
        var a = $("body > #wrapper").height() - 61;
        $(".sidebard-panel").css("min-height", a + "px");
    }

    $("#side-menu").metisMenu();
    $(".right-sidebar-toggle").click(function() {
        $("#right-sidebar").toggleClass("sidebar-open");
    });

    $(".sidebar-container").slimScroll({
        height: "100%",
        railOpacity: 0.4,
        wheelStep: 10
    });
    $(".open-small-chat").click(function() {
        $(this).children().toggleClass("fa-comments").toggleClass("fa-remove");
        $(".small-chat-box").toggleClass("active");
    });

    $(".small-chat-box .content").slimScroll({
        height: "234px",
        railOpacity: 0.4
    });

    $(".check-link").click(function() {
        var a = $(this).find("i"),e = $(this).next("span");
        return a.toggleClass("fa-check-square").toggleClass("fa-square-o"),e.toggleClass("todo-completed"),!1
    });

    $(function() {
        $(".sidebar-collapse").slimScroll({
            height: "100%",
            railOpacity: 0.9
        })
    });
    $(".navbar-minimalize").click(function() {
        $("body").toggleClass("mini-navbar");
        SmoothlyMenu();
    });

    a();
    $(window).bind("load resize click scroll",function() {
    	if (!$("body").hasClass('body-small')) {
    		a();
    	}
    });

    $(window).scroll(function() {
        $(window).scrollTop() > 0 && !$("body").hasClass("fixed-nav") ? $("#right-sidebar").addClass("sidebar-top") : $("#right-sidebar").removeClass("sidebar-top");
    });
    $(".full-height-scroll").slimScroll({
        height: "100%"
    });
    $("#side-menu>li").click(function() {
        $("body").hasClass("mini-navbar") && NavToggle();
    });
    $("#side-menu>li li a").click(function() {
        $(window).width() < 769 && NavToggle();
    });
    $(".nav-close").click(NavToggle);
    
    if(localStorageSupport){
    	var collapse=localStorage.getItem("collapse_menu");
    	var fixednavbar=localStorage.getItem("fixednavbar");
    	var boxedlayout=localStorage.getItem("boxedlayout");
    	var skin = localStorage.getItem("skin");
    	if(collapse=="on"){$("body").addClass("mini-navbar");SmoothlyMenu();}
    	if(fixednavbar=="on"){$(".navbar-static-top").removeClass("navbar-static-top").addClass("navbar-fixed-top");$("body").removeClass("boxed-layout");$("body").addClass("fixed-nav");}
    	if(boxedlayout=="on"){$("body").addClass("boxed-layout");$(".navbar-fixed-top").removeClass("navbar-fixed-top").addClass("navbar-static-top");$("body").removeClass("fixed-nav");}
    	if(skin == '1'){$("body").addClass("skin-1");}
        else if(skin == '2'){$("body").addClass("skin-2");}
        else if(skin == '3'){$("body").addClass("skin-3");}
    };
    
    $(".J_tabRefresh").click(function() {
    	var t = $('.J_iframe[data-id="' + $(".J_menuTab.active").data("id") + '"]'),e = t.attr("src"),a = layer.load();
        t.attr("src", e).load(function() {
            layer.close(a);
        })
    });
})

$(window).bind("load resize",function() {
    $(this).width() < 769 && ($("body").addClass("mini-navbar"), $(".navbar-static-side").fadeIn());
});